using BinaryProvider # requires BinaryProvider 0.3.0 or later

# Parse some basic command-line arguments
const verbose = "--verbose" in ARGS
const prefix = Prefix(get([a for a in ARGS if a != "--verbose"], 1, joinpath(@__DIR__, "usr")))
products = [
    LibraryProduct(prefix, String["libproj"], :libproj),
    FileProduct(prefix, "share/proj/CH", :ch),
    FileProduct(prefix, "share/proj/epsg", :epsg),
    FileProduct(prefix, "share/proj/esri", :esri),
    FileProduct(prefix, "share/proj/esri.extra", :esri_extra),
    FileProduct(prefix, "share/proj/GL27", :gl27),
    FileProduct(prefix, "share/proj/IGNF", :ignf),
    FileProduct(prefix, "share/proj/nad27", :nad27),
    FileProduct(prefix, "share/proj/nad83", :nad83),
    FileProduct(prefix, "share/proj/nad.lst", :nad_lst),
    FileProduct(prefix, "share/proj/other.extra", :other_extra),
    FileProduct(prefix, "share/proj/proj_def.dat", :proj_def_dat),
    FileProduct(prefix, "share/proj/world", :world),
]

# Download binaries from hosted location
bin_prefix = "https://github.com/JuliaGeo/PROJBuilder/releases/download/v4.9.3-1"

# Listing of files generated by BinaryBuilder:
download_info = Dict(
    Linux(:aarch64, :glibc) => ("$bin_prefix/PROJ.aarch64-linux-gnu.tar.gz", "8ff89f480442aaaf4b1959a8c7ad042b6716e9b88ab0b9681c3d58732e924b27"),
    Linux(:armv7l, :glibc, :eabihf) => ("$bin_prefix/PROJ.arm-linux-gnueabihf.tar.gz", "51b4388306ae43cf12acc72684e06756e44a01f9e7f183baeb7cc012201d23b7"),
    Linux(:i686, :glibc) => ("$bin_prefix/PROJ.i686-linux-gnu.tar.gz", "91991672d963824408a3f223d19bf16d4fe0999baf4dd95c35b7bc4ef7f7fbeb"),
    Windows(:i686) => ("$bin_prefix/PROJ.i686-w64-mingw32.tar.gz", "ceef81114d13fa876713eb4aefc76c9bfae570d619e5d989c0c7414c7b55544e"),
    Linux(:powerpc64le, :glibc) => ("$bin_prefix/PROJ.powerpc64le-linux-gnu.tar.gz", "4cbac1735b70da52f7bd77806323266ef28fa6dd151424f24875028a525c610e"),
    MacOS(:x86_64) => ("$bin_prefix/PROJ.x86_64-apple-darwin14.tar.gz", "11047f48eaddc207d1c11b81581cb39dbad6dc8caf22be7bd3bede9f80044958"),
    Linux(:x86_64, :glibc) => ("$bin_prefix/PROJ.x86_64-linux-gnu.tar.gz", "cc8e00301b854dfae1ee31363df01222b72414b99ea5455c2a37aab1b1ee29ba"),
    Windows(:x86_64) => ("$bin_prefix/PROJ.x86_64-w64-mingw32.tar.gz", "b12cac7e1d00df2ad7338e0b635a0f583641897691142e3c5b8ac02c6cc444c7"),
)

# Install unsatisfied or updated dependencies:
unsatisfied = any(!satisfied(p; verbose=verbose) for p in products)
if haskey(download_info, platform_key())
    url, tarball_hash = download_info[platform_key()]
    if unsatisfied || !isinstalled(url, tarball_hash; prefix=prefix)
        # Download and install binaries
        install(url, tarball_hash; prefix=prefix, force=true, verbose=verbose)
    end
elseif unsatisfied
    # If we don't have a BinaryProvider-compatible .tar.gz to download, complain.
    # Alternatively, you could attempt to install from a separate provider,
    # build from source or something even more ambitious here.
    error("Your platform $(triplet(platform_key())) is not supported by this package!")
end

# Write out a deps.jl file that will contain mappings for our products
write_deps_file(joinpath(@__DIR__, "deps.jl"), products)
